(23, 1, '2020-07-14 21:09:21', '2020-07-14 21:09:21', '<!-- wp:heading {\"align\":\"center\",\"textColor\":\"black\"} -->\n<h2 class=\"has-black-color has-text-color has-text-align-center\"><em>Get In Touch</em></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"textColor\":\"black\",\"customFontSize\":19} -->\n<p style=\"font-size:19px\" class=\"has-text-color has-text-align-center has-black-color\">If you have any questions or comments please complete the form below.<br>We’d love to hear from you</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:uagb/section {\"classMigrate\":true,\"block_id\":\"ea36dc1e\",\"topPadding\":0,\"bottomPadding\":0,\"leftPadding\":234,\"rightPadding\":156} -->\n<section class=\"wp-block-uagb-section uagb-section__wrap uagb-section__background-undefined uagb-block-ea36dc1e\"><div class=\"uagb-section__overlay\"></div><div class=\"uagb-section__inner-wrap\"><!-- wp:shortcode -->\n[contact-form-7 id=\"5\" title=\"Contact form 1\"]\n<!-- /wp:shortcode --></div></section>\n<!-- /wp:uagb/section -->', 'Contact Us', '', 'publish', 'closed', 'closed', '', 'contact-us', '', '', '2020-07-16 15:12:16', '2020-07-16 15:12:16', '', 0, 'http://localhost/wordpress-quiz/?page_id=23', 0, 'page', '', 0),
