(51, 1, '2020-07-15 06:07:15', '2020-07-15 06:07:15', '<!-- wp:heading {\"align\":\"center\"} -->\n<h2 class=\"has-text-align-center\"><strong>WordPress MCQ Quiz&nbsp;</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"textColor\":\"black\"} -->\n<p class=\"has-text-color has-black-color\">We have few WordPress MCQ&nbsp;test that checks your basic knowledge of WordPress Framework. This&nbsp;Wordpress Test contains around 20 questions of multiple choice type with 4 options. You have to select the right answer to a question .You must register before you can submit your quiz</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:uagb/section {\"classMigrate\":true,\"block_id\":\"59418092\"} -->\n<section class=\"wp-block-uagb-section uagb-section__wrap uagb-section__background-undefined uagb-block-59418092\"><div class=\"uagb-section__overlay\"></div><div class=\"uagb-section__inner-wrap\"><!-- wp:paragraph -->\n<p>[qsm quiz=1]</p>\n<!-- /wp:paragraph --></div></section>\n<!-- /wp:uagb/section -->', 'QUIZ', '', 'inherit', 'closed', 'closed', '', '42-revision-v1', '', '', '2020-07-15 06:07:15', '2020-07-15 06:07:15', '', 42, 'http://localhost/wordpress-quiz/2020/07/15/42-revision-v1/', 0, 'revision', '', 0),
(52, 1, '2020-07-15 06:08:00', '2020-07-15 06:08:00', '<!-- wp:heading {\"align\":\"center\",\"textColor\":\"black\"} -->\n<h2 class=\"has-black-color has-text-color has-text-align-center\">Get In Touch</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"textColor\":\"black\"} -->\n<p class=\"has-text-color has-text-align-center has-black-color\">If you have any questions or comments please complete the form below.<br>We’d love to hear from you</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:uagb/section {\"classMigrate\":true,\"block_id\":\"4ab4c8e4\"} -->\n<section class=\"wp-block-uagb-section uagb-section__wrap uagb-section__background-undefined uagb-block-4ab4c8e4\"><div class=\"uagb-section__overlay\"></div><div class=\"uagb-section__inner-wrap\"><!-- wp:shortcode -->\n[contact-form-7 id=\"5\" title=\"Contact form 1\"]\n<!-- /wp:shortcode --></div></section>\n<!-- /wp:uagb/section -->', 'CONTACT US', '', 'inherit', 'closed', 'closed', '', '23-revision-v1', '', '', '2020-07-15 06:08:00', '2020-07-15 06:08:00', '', 23, 'http://localhost/wordpress-quiz/2020/07/15/23-revision-v1/', 0, 'revision', '', 0),
